import styled from 'styled-components';
export const SocialProfile = styled.div`
  width: 300px;
  border-radius: 4px;
  text-align: center;
  padding-top: 20px;
  margin-top: 50px;
`;

export const Description = styled.div`
  margin-top: 10px;
  font-size: 25px;
  font-weight: bold;
  color: #898989;
`;

export const Avatar = styled.img`
  border: 1px solid black;
  border-radius: 50%;
  width: 240px;
`;

export const Stats = styled.ul`
  display: flex;
  margin-top: 15px;
  list-style-type: none;
  margin: 0;
  padding: 5px;
  justify-content: space-between;
  font-weight: bold;
  color: #898989;
`;

export const StatsInfo = styled.li`
  font-size: 20px;
  margin-right: 10px;
`;
export const StatsQuantity = styled.span`
  margin-left: 5px;
`;
